  public int longestValidParentheses(String s) {
    int maxLength = 0;
    int length = s.length();
    Stack<Integer> stack = new Stack<>();
    for (int i = 0; i < length; i++) {
      if (isLeftPart(s.charAt(i))) {
        stack.push(i);
      } else {
        if (stack.isEmpty() || isRightPart(s.charAt(stack.peek().intValue()))) {
          stack.push(i);
        } else {
          stack.pop();
        }
      }
    }
    if (stack.isEmpty()) {
      maxLength = length;
    } else {
      int lowPos = 0;
      int highPos = length;

      while (!stack.isEmpty()) {
        lowPos = stack.pop();
        maxLength = Math.max(maxLength, highPos - lowPos - 1);
        highPos = lowPos;
      }
      maxLength = Math.max(maxLength, highPos);

    }

    return maxLength;
  }

