  public boolean isParentheses(String s) {
    int length = s.length();
    if (length % 2 != 0) {
      return false;
    }

    Stack<Character> stack = new Stack<>();
    for (int i = 0; i < length; i++) {
      Character c = s.charAt(i);
      if (isLeftPart(c)) {
        stack.push(c);
      } else {
        // no pair for this right part
        if (stack.empty()) {
          return false;
        }

        if (!isCouple(stack.pop(), c)) {
          return false;
        }
      }
    }

    if (stack.empty()) {
      return true;
    }

    return false;
  }

  private boolean isLeftPart(Character c) {
    if (c == '(') {
      return true;
    }

    return false;
  }

  private boolean isRightPart(Character c) {
    if (c == ')') {
      return true;
    }

    return false;
  }

  private boolean isCouple(Character pop, Character c) {
    if ((c == ')' && pop == '(')) {
      return true;
    }
    return false;
  }

